import React from "react";

const useLocalStorage=()=>{
    function getStorage(){
        const listget:string =localStorage.getItem('data');
        if(listget){
            return JSON.parse(localStorage.getItem('data'));
        }else{
            return []
        }
    }

    return getStorage;
}
export default useLocalStorage;
