import { RiEdit2Fill, RiUpload2Line } from "react-icons/ri";
import { useSelector } from "react-redux";
//import useDetailsAPI from "../../API/DetailsAPI";
import { useState } from "react";
import "../style/app.css";
import firebase ,{storage} from "../../Firebase/firebase_config";
import store from "../../store";


const Details = () => {
  //const List = useDetailsAPI();
  const Id = JSON.parse(Object(window.localStorage.getItem("Info"))).id;
  const [email, setEmail] = useState<string>("");
  const [name, setName] = useState<string>("");
  const [city, setCity] = useState<string>("");
  const [state, setState] = useState<string>("");
  const [country, setCountry] = useState<string>("");
  const [phone, setPhone] = useState<string>("");
  const [emailButton, setEmailButton] = useState<boolean>(false);
  const [cityButton, setCityButton] = useState<boolean>(false);
  const [nameButton, setNameButton] = useState<boolean>(false);
  const [stateButton, setStateButton] = useState<boolean>(false);
  const [phoneButton, setPhoneButton] = useState<boolean>(false);
  const [countryButton, setCountryButton] = useState<boolean>(false);
  const [editButton, setEditButton] = useState<boolean>(false);
  const [image, setImage] = useState<any>(null);
  const [upload,setUpload]=useState<boolean>(false);
  const mystate = useSelector((state: any) => state.Fetch);
  
  console.log("getstate",store.getState());
  //console.log("mystate",mystate)
  
  function getEmail(event: {
    target: { value: React.SetStateAction<string> };
  }) {
    setEmailButton(false);
    setEmail(event.target.value);
  }

  function getCity(event: { target: { value: React.SetStateAction<string> } }) {
    setCity(event.target.value);
    setCityButton(false);
  }
  function getName(event: { target: { value: React.SetStateAction<string> } }) {
    setName(event.target.value);
    setNameButton(false);
  }
  function getState(event: {
    target: { value: React.SetStateAction<string> };
  }) {
    setState(event.target.value);
    setStateButton(false);
  }
  function getPhone(event: {
    target: { value: React.SetStateAction<string> };
  }) {
    setPhone(event.target.value);
    setPhoneButton(false);
  }

  function getCountry(event: {
    target: { value: React.SetStateAction<string> };
  }) {
    setCountry(event.target.value);
    setCountryButton(false);
  }

  const editProfile = () => {
    setEmailButton(true);
    setCountryButton(true);
    setCityButton(true);
    setPhoneButton(true);
    setStateButton(true);
    setNameButton(true);
    setEditButton(true);
  };
  const saveProfile = () => {
    const Userref = firebase.database().ref("User");
    if (!countryButton) {
      Userref.child(Id).update({
        Country: country,
      });
    }
    if (!emailButton) {
      Userref.child(Id).update({
        email: email,
      });
    }
    if (!cityButton) {
      Userref.child(Id).update({
        City: city,
      });
    }
    if (!nameButton) {
      Userref.child(Id).update({
        Name: name,
      });
    }
    if (!stateButton) {
      Userref.child(Id).update({
        State: state,
      });
    }
    if (!phoneButton) {
      Userref.child(Id).update({
        Phone: phone,
      });
    }

    Userref.child(Id).update({});
    setEditButton(false);
  };

  const onImageChange = (e:any) => {
    const reader = new FileReader();
    console.log("reader",reader);
    let file = e.target.files[0]; 
    if (file) {
      reader.onload = () => {
        if (reader.readyState === 2) {
          console.log(file);
          setImage(file);
        }
      };
      console.log("reader.readAsDataURL(e.target.files[0])",reader.readAsDataURL(e.target.files[0]));
      reader.readAsDataURL(e.target.files[0]);
      setUpload(true);
    } else {
      setImage(null);
    }
  };
  const removeImage=()=>{
    const Userref = firebase.database().ref("User");
    Userref.child(Id).update({
      Imageurl:"https://www.computerhope.com/jargon/g/guest-user.jpg",
    })
  }
  const uploadToFirebase = () => {
    if (image) {
      const storageRef = storage.ref();
      const imageRef = storageRef.child(image.name);
      imageRef.put(image)
      imageRef.getDownloadURL().then((url) => {
        alert("Image uploaded successfully to Firebase.");
        const Userref = firebase.database().ref("User");
        Userref.child(Id).update({
        Imageurl:url,
        
    })
    setUpload(false)
    });
    } else {
      alert("Please upload an image first.");
      
    setUpload(false)
    }
  };
  return (
    <>
      {mystate?
          <div className="Detailsdiv">
            <div className="Deatilsimage">
              <div>
              <label htmlFor="image"><span><RiUpload2Line /></span></label>
              <input id="image"  type="file" accept="image/*" onChange={(e) => {onImageChange(e); }} />
              <img src={mystate[0].Imageurl} alt="UserImage" width="170" height="170" />
              {upload ? <button className="Uploadbutton" onClick={uploadToFirebase}>Upload</button>: null }
              
              {!upload ? <button className="Uploadbutton" onClick={removeImage}>Remove</button>: null }
              </div>
              <div className="Ratingheading">
                <h5>YOUR RATING</h5>
                <h5>ACTIVITY</h5>
              </div>
              <div className="Ratingvalue">
                <h5> 54%</h5>
                <h5>93%</h5>
              </div>
            </div>
            <div>
              <div className="Detailsname">
                {editButton ? (
                  <div>
                    {nameButton ? (
                      <input
                        className="Nameinput"
                        type="text"
                        value={mystate[0].Name}
                        onChange={getName}
                        required
                      />
                    ) : (
                      <input
                        className="Nameinput"
                        type="text"
                        value={name}
                        onChange={getName}
                        required
                      />
                    )}
                  </div>
                ) : (
                  <h3 style={{ marginLeft: "20px" }}> {mystate[0].Name}</h3>
                )}

                {editButton ? (
                  <button onClick={saveProfile}>
                    <span>
                      <RiEdit2Fill />
                    </span>
                    Save
                  </button>
                ) : (
                  <button onClick={editProfile}>
                    <span>
                      <RiEdit2Fill />
                    </span>
                    EDIT
                  </button>
                )}
              </div>
              <h5 className="Namedetails">
                {mystate[0].City}, {mystate[0].State}
              </h5>

              <div className="Details">
                <div style={{ marginTop: "13px" }}>
                  <h4>Email</h4>
                  {editButton ? (
                    <div>
                      {emailButton ? (
                        <input
                        className="Emailinput"
                          style={{ marginLeft: "35px" }}
                          type="email"
                          value={mystate[0].email}
                          onChange={getEmail}
                          required
                        />
                      ) : (
                        <input
                        className="Emailinput"
                          style={{ marginLeft: "35px" }}
                          type="email"
                          value={email}
                          onChange={getEmail}
                          required
                        />
                      )}
                    </div>
                  ) : (
                    <h5 style={{ marginLeft: "35px" }}> {mystate[0].email}</h5>
                  )}
                </div>

                <div>
                  <h4>City</h4>
                  {editButton ? (
                    <div>
                      {cityButton ? (
                        <input
                        className="Cityinput"
                          style={{ marginLeft: "45px" }}
                          value={mystate[0].City}
                          onChange={getCity}
                          required
                        />
                      ) : (
                        <input
                        className="Cityinput"
                          style={{ marginLeft: "45px" }}
                          type="text"
                          value={city}
                          onChange={getCity}
                          required
                        />
                      )}
                    </div>
                  ) : (
                    <h5 style={{ marginLeft: "45px" }}> {mystate[0].City}</h5>
                  )}
                </div>
                <div>
                  <h4>State</h4>
                  {editButton ? (
                    <div>
                      {stateButton ? (
                        <input
                        className="Stateinput"
                          style={{ marginLeft: "37px" }}
                          value={mystate[0].State}
                          type="text"
                          onChange={getState}
                          required
                        />
                      ) : (
                        <input
                        className="Stateinput"
                          style={{ marginLeft: "37px" }}
                          type="text"
                          value={state}
                          onChange={getState}
                          required
                        />
                      )}
                    </div>
                  ) : (
                    <h5 style={{ marginLeft: "37px" }}> {mystate[0].State}</h5>
                  )}
                </div>
                <div>
                  <h4>Country</h4>
                  {editButton ? (
                    <div>
                      {countryButton ? (
                        <input
                        className="Countryinput"
                          style={{ marginLeft: "18px" }}
                          value={mystate[0].Country}
                          type="text"
                          onChange={getCountry}
                          required
                        />
                      ) : (
                        <input
                        className="Countryinput"
                          style={{ marginLeft: "18px" }}
                          type="text"
                          value={country}
                          onChange={getCountry}
                          required
                        />
                      )}
                    </div>
                  ) : (
                    <h5 style={{ marginLeft: "18px" }}> {mystate[0].Country}</h5>
                  )}
                </div>
                <div>
                  <h4>Phone</h4>
                  {editButton ? (
                    <div>
                      {phoneButton ? (
                        <input
                        className="Phoneinput"
                          style={{ marginLeft: "28px" }}
                          value={mystate[0].Phone}
                          type="text"
                          onChange={getPhone}
                          required
                        />
                      ) : (
                        <input
                        
                        className="Phoneinput"
                          style={{ marginLeft: "28px" }}
                          type="text"
                          value={phone}
                          onChange={getPhone}
                          required
                        />
                      )}
                    </div>
                  ) : (
                    <h5 style={{ marginLeft: "28px" }}> {mystate[0].Phone}</h5>
                  )}
                </div>
              </div>
            </div>
          </div>
          : <h2>Loading...</h2>}
    </>
  );
};
export default Details;
