import React, { useState } from "react";
import { NavLink, useHistory, withRouter } from "react-router-dom";
import "../style/app.css";
import firebase from "../../Firebase/firebase_config";
import useRegisterAPI from "../../API/RegisterAPI";
import SimpleCrypto from "simple-crypto-js";

const Register = () => {
  const List: any = useRegisterAPI();
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string | number>("");
  const [cpassword, cSetPassword] = useState<string | number>("");
  const [name, setName] = useState<string>("");
  const [city, setCity] = useState<string>("");
  const [state, setState] = useState<string>("");
  const [country, setCountry] = useState<string>("");
  const [phone, setPhone] = useState<string>("");
  const [complete, setcomplete] = useState<boolean>(false);
  const [error, seterror] = useState<string | null>("");
  const history = useHistory();
  var _secretKey = "1234p@1234";
  var simpleCrypto:any = new SimpleCrypto(_secretKey);


  function getEmail(event: {
    target: { value: React.SetStateAction<string> };
  }) {
    setEmail(event.target.value);
    seterror(null);
  }
  function getPassword(event: {
    target: { value: React.SetStateAction<string | number> };
  }) {
    setPassword(event.target.value);
    seterror(null);
  }
  function getCPassword(event: {
    target: { value: React.SetStateAction<string | number> };
  }) {
    cSetPassword(event.target.value);
    seterror(null);
  }
  function getCity(event: { target: { value: React.SetStateAction<string> } }) {
    setCity(event.target.value);
    seterror(null);
  }
  function getName(event: { target: { value: React.SetStateAction<string> } }) {
    setName(event.target.value);
    seterror(null);
  }
  function getState(event: {
    target: { value: React.SetStateAction<string> };
  }) {
    setState(event.target.value);
    seterror(null);
  }
  function getPhone(event: {
    target: { value: React.SetStateAction<string> };
  }) {
    setPhone(event.target.value);
    seterror(null);
  }

  function getCountry(event: {
    target: { value: React.SetStateAction<string> };
  }) {
    setCountry(event.target.value);
    seterror(null);
  }
  const handle = (event: any) => {
    event.preventDefault();
    let pass = true;

    List.map((item: { email: any }) => {
      if (String(item.email) === email) {
        pass = false;
        return seterror("User already exist!");
      } else {
        return 0;
      }
    });
    if (pass === true) {
      if (password !== cpassword) {
        seterror("Password dose not match");
      } else {
        const cipherText = simpleCrypto.encrypt(password)
        console.log(cipherText)
        const todoref = firebase.database().ref("User");
        const todo = {
          Name: name,
          email: email,
          password:password,
          City: city,
          State: state,
          Country: country,
          Phone: phone,
          Imageurl: "https://www.computerhope.com/jargon/g/guest-user.jpg",
        };
        todoref.push(todo);
        setcomplete(true);
        setEmail("");
        setPassword("");
        cSetPassword("");
        setCity("");
        setCountry("");
        setName("");
        setPhone("");
        setState("");
        seterror("User created! Click on Login");
      }
    }
  };

  return (
    <div className="Registerdiv">
      <form onSubmit={handle}>
        <div style={{ marginLeft: "130px" }}>
          <h2>Registration</h2>
        </div>
        <div>
          <label htmlFor="Name" id="Name" className="Registerlabel">
            Name
          </label>
          <input
            className="Registerinput1"
            type="text"
            name="Name"
            placeholder="Enter Your Name"
            value={name}
            onChange={getName}
            required
          />
        </div>

        <div>
          <label htmlFor="email" id="email" className="Registerlabel">
            Email
          </label>
          <input
            className="Registerinput2"
            type="email"
            name="email"
            placeholder="Enter Your E-mail id"
            value={email}
            onChange={getEmail}
            required
          />
        </div>
        <br />
        <div>
          <label htmlFor="password" id="password" className="Registerlabel">
            Password
          </label>
          <input
            className="Registerinput3"
            type="password"
            name="password"
            placeholder="Enter Your Password"
            value={password}
            onChange={getPassword}
            required
          />
        </div>

        <div>
          <label htmlFor="cpassword" id="cpassword" className="Registerlabel">
            Confirm Password
          </label>
          <input
            className="Registerinput4"
            type="password"
            name="cpassword"
            placeholder="Enter Your Confirm Password"
            value={cpassword}
            onChange={getCPassword}
            required
          />
        </div>
        <div>
          <label htmlFor="city" id="city" className="Registerlabel">
            City
          </label>
          <input
            className="Registerinput5"
            type="text"
            name="city"
            value={city}
            placeholder="Enter Your City"
            onChange={getCity}
            required
          />
        </div>

        <div>
          <label htmlFor="state" id="state" className="Registerlabel">
            State
          </label>
          <input
            className="Registerinput6"
            type="text"
            name="state"
            placeholder="Enter Your State"
            value={state}
            onChange={getState}
            required
          />
        </div>

        <div>
          <label htmlFor="country" id="country" className="Registerlabel">
            Country
          </label>
          <input
            className="Registerinput7"
            type="text"
            name="country"
            value={country}
            placeholder="Enter Your Country"
            onChange={getCountry}
            required
          />
        </div>

        <div>
          <label htmlFor="phone" id="phone" className="Registerlabel">
            Phone
          </label>
          <input
            className="Registerinput8"
            type="number"
            name="phone"
            value={phone}
            placeholder="Enter Your Phone Number"
            onChange={getPhone}
            required
          />
        </div>

        <div className="Registerbuttondiv">
          {complete ? (
            () => {
              history.push("/");
            }
          ) : (
            <button className="Registerbutton" type="submit">
              Signup
            </button>
          )}
          <button type="button" className="Registerloginbutton">
            <NavLink
              to="/"
              style={{ textDecoration: "none" }}
              className="Registerloginbuttonlink"
            >
              Login
            </NavLink>
          </button>
        </div>
        {complete ? (
          <div className="Errorkdiv1">
            <h3 className="Errorheading1">{error}</h3>
          </div>
        ) : (
          <div className="Errorkdiv">
            <h3 className="Errorheading">{error}</h3>
          </div>
        )}
      </form>
    </div>
  );
};

export default withRouter(Register);
