import { useEffect, useState } from "react";
import firebase from "./firebase_config";

const useDetailsAPI=()=>{
    const [list,setList]=useState<any[]>([]);
    const [newdata,setNewData]=useState<any>();
    
    useEffect(()=>{
        const Userdata=firebase.database().ref("User");
        Userdata.on("value",(snapshot)=>{
            let USER=snapshot.val();
            const userList=[];
            for(const key in USER){
                if (typeof(window.localStorage.getItem("Info")) === 'object'){
                    setNewData(Object(window.localStorage.getItem("Info")).id)
                }
                else if (typeof(window.localStorage.getItem("Info")) === 'string'){
                    setNewData(JSON.parse(JSON.stringify(window.localStorage.getItem("Info"))).id)
                }
                if(key===(newdata)){
                userList.push({
                    id:key,
                    email:String(USER[key].email),
                    password:String(USER[key].password),
                    Name:String(USER[key].Name),
                    City:String(USER[key].City),
                    State:String(USER[key].State),
                    Country:String(USER[key].Country),
                    Phone:String(USER[key].Phone),
                    
                })}
            }
            setList(userList);
            //console.log("DetialsUserList",userList);
        })
        },[])
    
    return list;

}
export default useDetailsAPI;
