import React from "react";

const useLocalStorage=()=>{
    const listget =localStorage.getItem('data');
    function getStorage(){
        if(listget){
            return JSON.parse(localStorage.getItem('data'));
        }else{
            return []
        }
    }

    return(getStorage)
}
export default useLocalStorage;
