import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import ToList from "./TodoList";
import "./app.css";


const App=()=>{
    const [newInputData,setNewInputData]=useState(null);
    const [list, setList] = useState([]);
    
    function getValue(event){
        setNewInputData(event.target.value);
        
    };
    const updateData=()=>{
        setList((objitem)=>{
            return [...objitem,newInputData];
        })
        setNewInputData(""); //For empty the input box  with provide value 
    };
    return (
    <div className="Divbody">
        <h3>TODO LIST</h3>
        <label htmlFor="input">Enter Item</label>
        <input className="item" value={newInputData} id="input" onChange={getValue} />
        <ol className="show">
            { list.map((listitems)=>{
                return <ToList text={listitems} />
            })}
        </ol>
        <button onClick={updateData}>ADD</button>
    </div>
    );
};

ReactDOM.render(<App />,document.querySelector('#root'));
