import { RiEdit2Fill, RiUpload2Line } from "react-icons/ri";
import { useSelector } from "react-redux";
import './app.css'
import image from './img.jpg';
import useDetailsAPI from "./DetailsAPI";
import { useState } from "react";
import firebase from "./firebase_config";

const Details = () => {
    const List=useDetailsAPI();
    const [email,setEmail]=useState<string>("");
    const [password,setPassword]=useState<string | number >("");
    const [name,setName]=useState<string>("")
    const [city,setCity]=useState<string>("")
    const [state,setState]=useState<string>("")
    const [country,setCountry]=useState<string>("")
    const [phone,setPhone]=useState<string>("")
    const [emailButton,setEmailButton]=useState<boolean>(false)
    const Id=(JSON.parse(Object(window.localStorage.getItem("Info"))).id);
    const [cityButton,setCityButton]=useState<boolean>(false);
    const [nameButton,setNameButton]=useState<boolean>(false);
    const [stateButton,setStateButton]=useState<boolean>(false);
    const [phoneButton,setPhoneButton]=useState<boolean>(false);
    const [countryButton,setCountryButton]=useState<boolean>(false);
    const [editButton,setEditButton]=useState<boolean>(false)
    const mystate=useSelector((state:any)=>state.Fetch);
    
    //console.log("Detials List",JSON.stringify(List))
    //console.log("Id is",Id);
    console.log("List is",List)
    function getEmail(event: { target: { value: React.SetStateAction<string>; }; }){   
        setEmailButton(false);
        setEmail(event.target.value);
    }
      function getPassword(event: { target: { value: React.SetStateAction<string | number>; }; }){   
        setPassword(event.target.value);
        setEmailButton(false);
    }
    function getCity(event: { target: { value: React.SetStateAction<string>; }; }){   
        setCity(event.target.value);
        setCityButton(false);
    }
    function getName(event: { target: { value: React.SetStateAction<string>; }; }){   
        setName(event.target.value);
        setNameButton(false);
    }
    function getState(event: { target: { value: React.SetStateAction<string>; }; }){   
        setState(event.target.value);
        setStateButton(false);
    }
    function getPhone(event: { target: { value: React.SetStateAction<string>; }; }){   
        setPhone(event.target.value);
        setPhoneButton(false);
    }
    
    function getCountry(event: { target: { value: React.SetStateAction<string>; }; }){   
            
        console.log("Country value",event.target.value)
        setCountry(event.target.value);
        setCountryButton(false);        
    
        
    }

    const editProfile=()=>{
        setEmailButton(true);
        setCountryButton(true);
        setCityButton(true);
        setPhoneButton(true);
        setStateButton(true);
        setNameButton(true);
        setEditButton(true);
    }
    const saveProfile=()=>{
        const Userref=firebase.database().ref("User");
        if(!countryButton){
            Userref.child(Id).update({
                Country:country,
            })}
            if (!emailButton) {
              Userref.child(Id).update({
                email:email,
              });
            }
            if (!cityButton) {
              Userref.child(Id).update({
                City:city,
              });
            }
            if (!nameButton) {
              Userref.child(Id).update({
                Name:name,
              });
            }
            if (!stateButton) {
              Userref.child(Id).update({
                State:state,
              });
            }
            if(!phoneButton){
                Userref.child(Id).update({
                    Phone:phone,
                })}
            
                                                
        Userref.child(Id).update({
        })
        setEditButton(false)
    }
    
  return <>
         {List.map((profile: any)=>{
             return <div className="Detailsdiv">
                <div className="Deatilsimage">
                    <div>
                    <span title="Update"><RiUpload2Line/></span>
                    <img src={image} alt="User" width="170" height="170"/>
                    </div>
                    <div className="Ratingheading">
                    <h5>YOUR RATING</h5>
                    <h5>ACTIVITY</h5>
                    </div>
                <div className="Ratingvalue">
                    <h5> 54%</h5>
                    <h5>93%</h5>
                </div>
                </div>
                <div >
                    <div className="Detailsname">
                        {editButton
                        ?
                        <div>
                        {nameButton ?
                        <input  type ="text" value={profile.Name} onChange={getName} required/>:
                        <input  type ="text" value={name} onChange={getName} required/> }
                        </div>
                        : <h3 style={{marginLeft:"20px"}}> {profile.Name}</h3>}
                        
                        {editButton
                        ?
                        <button onClick={saveProfile}><span><RiEdit2Fill /></span>Save</button>
                        :
                        <button onClick={editProfile}><span><RiEdit2Fill /></span>EDIT</button>}
                        </div>
                        <h5 className="Namedetails">{profile.City}, {profile.State}</h5>
                    
                    <div className="Details">
                        <div style={{marginTop:"13px"}}>
                        <h4>Email</h4>
                        {editButton
                        ?
                        <div>
                        {emailButton
                        ?<input style={{marginLeft:"35px"}} type ="email" value={profile.email} onChange={getEmail} required/>
                        :<input style={{marginLeft:"35px"}} type ="email" value={email} onChange={getEmail} required/>}
                        </div>
                        : <h5 style={{marginLeft:"35px"}}> {profile.email}</h5>}
                         
                        </div>
                        
                        <div >    
                        <h4>City</h4>
                        {editButton
                        ?
                        <div>
                        {cityButton
                         ?
                        <input style={{marginLeft:"35px"}}  value={profile.City} onChange={getCity} required/>
                        :
                        <input style={{marginLeft:"35px"}} type ="text" value={city} onChange={getCity} required/> }
                        </div>
                        : <h5 style={{marginLeft:"45px"}}> {profile.City}</h5>}
                        
                        </div>
                        <div>
                        <h4>State</h4>
                        {editButton
                        ?
                        <div>
                        {stateButton ?
                        <input style={{marginLeft:"37px"}} value={profile.State} type="text" onChange={getState} required/>:
                        <input style={{marginLeft:"37px"}} type ="text" value={state} onChange={getState} required/> }
                        </div>
                        : <h5 style={{marginLeft:"37px"}}> {profile.State}</h5>}
                        </div>
                        <div>
                        <h4>Country</h4>
                        {editButton
                        ?
                        <div>
                        {countryButton ?
                        <input style={{marginLeft:"18px"}} value={profile.Country} type="text" onChange={getCountry} required/>:
                        <input style={{marginLeft:"18px"}} type ="text" value={country} onChange={getCountry} required/> }
                        </div>
                        : <h5 style={{marginLeft:"18px"}}> {profile.Country}</h5>}
                        </div>
                        <div>
                        <h4>Phone</h4>
                        {editButton
                        ?
                        
                        <div>
                        {phoneButton ?
                        <input style={{marginLeft:"28px"}} value={profile.Phone} type="text" onChange={getPhone} required/>:
                        <input style={{marginLeft:"28px"}} type ="text" value={phone} onChange={getPhone} required/> }
                        </div>
                        : <h5 style={{marginLeft:"28px"}}> {profile.Phone}</h5>}
                        </div>
                        
                    </div>
                </div>     
        </div>   })}
  </>
};
export default Details;
