import React from "react";

const useLocalStorage=(items:object[])=>{
    function getStorage(items){
        if(items){
            return localStorage.getItem(JSON.parse(items));
        }else{
            return []
        }
    }

    return(getStorage(items))
}
export default useLocalStorage;
