import React, { useState,useEffect } from 'react';
import { NavLink, useHistory, withRouter } from 'react-router-dom';
import firebase from "./firebase_config";


const Register=()=>{
    const [email,setEmail]=useState<string>("");
    const [password,setPassword]=useState<string | number >("");
    const [cpassword,cSetPassword]=useState<string | number >("");
    const [complete,setcomplete]=useState<boolean>(false);
    const [error , seterror]=useState<string|null>("");    
    const [list,setList]=useState<any[]>([]);
    const history=useHistory();
    
    useEffect(()=>{
        const userFun= async ()=>{
        const responce=await fetch('https://todo-list-64f41-default-rtdb.firebaseio.com/User.json');
        const responseData = await responce.json();
        const userList=[];
        for(const key in responseData){
            userList.push({
                id:key,
                email:String(responseData[key].email),
                password:String(responseData[key].password),
            })
        }
        setList(userList);
        console.log(userList);
    };
    userFun();
    },[])
     
    function getValue1(event: { target: { value: React.SetStateAction<string>; }; }){   
        setEmail(event.target.value);
        seterror(null);

    }
      function getValue2(event: { target: { value: React.SetStateAction<string | number>; }; }){   
        setPassword(event.target.value);
        seterror(null);
    }
    function getValue3(event: { target: { value: React.SetStateAction<string | number>; }; }){   
        cSetPassword(event.target.value);
        seterror(null);
    }

    const handle=(event: any)=>{
        event.preventDefault();
        let pass=true;
        list.map((item)=>{
            //console.log("item email",item.email);
            if(String(item.email)===email){
                //console.log("item email",item.email);
                pass=false;
                return seterror("User already exist!")
            }
            else {
                return 0;
            }})
            if(pass===true){
                if(password!==cpassword){
                    seterror("Password dose not match");
                 }
                else{
                    const todoref = firebase.database().ref("User");
                    const todo ={email:email,password:password}
                    todoref.push(todo);
                    setcomplete(true);
                    setEmail("");
                    setPassword("");
                    cSetPassword("");
                    seterror("User created! Click on Login")
                    }
                }
        }
    
    return(
    <div className="Registerdiv">
        <form onSubmit={handle}>
            <div><h2>Signup</h2></div>
            <div>
                <label htmlFor="Name" id="Name" className="Registerlabel">Name</label>
                <input className="Registerinput3" type="text" name="Name" value={cpassword} onChange={getValue3} required/>
            </div> 
            
            <div>
                <label htmlFor="email" id="email" className="Registerlabel">Email</label>
                <input className="Registerinput1" type="email" name="email" value={email} onChange={getValue1} required/>
            </div>
            <br/>
            <div>
                <label htmlFor="password" id="password" className="Registerlabel">Password</label>
                <input className="Registerinput2" type="password" name="password" value={password} onChange={getValue2} required/>
            </div>   
            
            <div>
                <label htmlFor="cpassword" id="cpassword" className="Registerlabel">Confirm Password</label>
                <input className="Registerinput3" type="password" name="cpassword" value={cpassword} onChange={getValue3} required/>
            </div> 
            <div>
                <label htmlFor="cpassword" id="cpassword" className="Registerlabel">City</label>
                <input className="Registerinput3" type="password" name="cpassword" value={cpassword} onChange={getValue3} required/>
            </div> 
            
            <div>
                <label htmlFor="cpassword" id="cpassword" className="Registerlabel">State</label>
                <input className="Registerinput3" type="password" name="cpassword" value={cpassword} onChange={getValue3} required/>
            </div> 
            
            <div>
                <label htmlFor="cpassword" id="cpassword" className="Registerlabel">Country</label>
                <input className="Registerinput3" type="password" name="cpassword" value={cpassword} onChange={getValue3} required/>
            </div> 
            
            {console.log(complete)}
            <div className="Registerbuttondiv">
           {complete ? ()=>{history.push("/")} :<button className="Registerbutton" type="submit">Signup</button> }
           <button type="button" className="Registerloginbutton"><NavLink to="/" style={{ textDecoration: 'none' }} className="Registerloginbuttonlink">Login</NavLink></button>
           </div>
           {complete
            ?
           <div className="Errorkdiv1"><h3 className="Errorheading1">{error}</h3></div>
           :
           <div className="Errorkdiv"><h3 className="Errorheading">{error}</h3></div>
        }  
        </form>
    </div>
    )


}

export default withRouter(Register);
