import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import "./app.css";
import Input from './Input';
import Button from './button';

const App=()=>{
    const [newInputData,setNewInputData]=useState(null);
    const [list, setList] = useState([]);
    
    function getValue(event){   
        setNewInputData(event.target.value);
        
    };
    const updateData=()=>{
        if(!newInputData){
        }
        else
        {
        const allinput={id: new Date().getTime().toString(),name:newInputData};
        setList([...list,allinput]);
        setNewInputData("");  
        }
        };
    const deleteEle=(index)=>{
        const updatelist=list.filter((ele)=>{
            return index !== ele.id;
        });
        setList(updatelist)  
    }
    return (
    <div className="Divbody">
        <Input data={newInputData} value={getValue}/>
        <div>
            { list.map((listitems)=>{
                return (
                <div>
                <input type="checkbox"></input>
                <h3>{listitems.name}</h3>
                <i class="far fa-trash-alt"></i>
                </div>
                )
            
            })}
        </div>
        <Button data={updateData}/>
    </div>
    )
    }

ReactDOM.render(<App />,document.querySelector('#root'));
