import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import SimpleCrypto  from "simple-crypto-js";
import "../style/app.css";
var button = false;
const User = () => {
  const [email, updateemail] = useState<string>("");
  const [password, updatepassword] = useState<string>("");
  const [error, seterror] = useState<String | null>(null);
  const [friendList, setfriendList] = useState<any[]>([]);
  const history = useHistory();
  var _secretKey = "1234p@1234";
  var simpleCrypto:any = new SimpleCrypto(_secretKey);

  useEffect(() => {
    button = false;
    const userFun = async () => {
      const responce = await fetch(
        "https://todo-list-64f41-default-rtdb.firebaseio.com/User.json"
      );
      const responseData = await responce.json();
      const userList = [];

      for (const key in responseData) {
        userList.push({
          id: key,
          email: String(responseData[key].email),
          password: String(responseData[key].password),
        });
      }
      setfriendList(userList);
      
    };
    userFun();
  }, []);

  function getValue1(event: any) {
    updateemail(event.target.value);
    seterror(null);
  }
  function getValue2(event: any) {
    updatepassword(event.target.value);
    seterror(null);
  }

  function handle(e: any) {
    e.preventDefault();
    friendList.map((friend) => {
      console.log(simpleCrypto.decrypt(friend.password))
      console.log(password)
      if (friend.email === email && simpleCrypto.decrypt(friend.password) === password) {
      
        button = true;
        history.push("/status");
        window.localStorage.setItem("Info", JSON.stringify(friend));
      }
      return null;
    });
    if (!button) {
      seterror("Wrong User Id and Password");
    }
  }
  return (
    <div className="Loginmain">
      <div className="Logindiv">
        <form onSubmit={handle}>
          <div>
            <h2>Login Form</h2>
          </div>
          <div>
            <label htmlFor="email" id="email" className="Loginlabel">
              Email
            </label>
            <input
              className="Logininput1"
              type="email"
              name="email"
              value={email}
              onChange={getValue1}
              required
            />
          </div>
          <br />
          <div>
            <label htmlFor="password" id="password" className="Loginlabel">
              Password
            </label>
            <input
              className="Logininput2"
              type="password"
              name="password"
              value={password}
              onChange={getValue2}
              required
            />
          </div>
          <br />
          <div>
            <button className="Loginbutton" type="submit">
              Login
            </button>

            <button className="Loginregisterbutton">
              <Link
                to="/Register"
                style={{ textDecoration: "none" }}
                className="Registerloginbuttonlink"
              >
                Register
              </Link>
            </button>
          </div>
          {button ? (
            <div></div>
          ) : (
            <div className="Errorkdiv">
              <h2 className="Errorheading">{error}</h2>
            </div>
          )}
        </form>
      </div>
    </div>
  );
};
export default User;
